const char *html_page = "<!doctypehtml><html lang=pt-br><title>Gerenciador de Conexões</title><meta charset=utf-8><script>function enviarDados() {let ssid = document.getElementById('ssid-id').value, password = document.getElementById('password-id').value, ip = document.getElementById('ip-id').value, gateway = document.getElementById('gateway-id').value, netmask = document.getElementById('netmask-id').value, port = document.getElementById('port-id').value; let params = `ssid=${ssid}&password=${password}&ip=${ip}&gateway=${gateway}&netmask=${netmask}&port=${port}`; var xhttp = new XMLHttpRequest(); xhttp.open('POST', '/', true); xhttp.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded'); xhttp.onreadystatechange = function(){if(xhttp.readyState === XMLHttpRequest.DONE && xhttp.status === 200){if(xhttp.responseText === '1'){document.getElementById('msg-id').innerHTML = 'As alterações de rede e servidor foram salvas com sucesso. Reinicie o sistema.';}}}; xhttp.send(params);}</script><body><style>body{background-color:#730aec;font-family:Courier New,Arial;color:#fff}h1{text-align:center}h2{text-align:center;color:#ffa600}h3{margin:0 0 10px}.card{width:90%;margin:20px auto;padding:30px;border-radius:5px 50px;border:2px solid #fff;background-color:#07af3a}input{margin-bottom:15px;margin-left:1%;width:96%;border-radius:20px;padding:5px 15px;border:2px solid #fff;background-color:#ccccac;color:#10138b;font-weight:600}label{font-size:18px;margin-left:10px}button{border-radius:20px;border:2px solid #fff;background-color:#07af3a;color:#fff;padding:10px 20px;font-size:22px;font-family:Courier New,Arial;font-weight:600;min-width:200px;margin:30px 4%;float:inline-end}</style><h1>Gerenciador de Conexões</h1><h2 id=msg-id></h2><div class=card id=credential><h3>Login</h3><div><label for=ssid>SSID</label><br><input id=ssid-id name=ssid placeholder=Usuario><br><label for=password>Senha</label><br><input id=password-id name=password placeholder=********></div></div><div class=card id=port><h3>Server</h3><div><label for=ip>IP</label><br><input id=ip-id name=ip placeholder=192.168.1.1> <label for=gateway>Gateway</label><br><input id=gateway-id name=gateway placeholder=192.168.1.0> <label for=netmask>Netmask</label><br><input id=netmask-id name=netmask placeholder=255.255.255.0> <label for=port>Porta</label><br><input id=port-id name=port placeholder=6000></div></div><button onclick=enviarDados()>Salvar</button></body></html>";